import random

def nwd(a, b):
    while b:
        a, b = b, a%b
    return a

def euklides(n, x):
    a, b, u, v = n, x, 0, 1
    while b != 0:
        q = a // b
        a, b = b, a + (-q * b)
        u, v = v, u + (-q * v)
    d = a
    v = (d - x * u) // n
    return u, v, d


def zadanie1(n, b):
    g, x, y = euklides(n, b)
    if y == 1:
        return g % n
    else:
        return False

def zadanie2(x, k, p):
    a = 1
    b = x

    while k > 0:
        if k % 2 == 1:
            a = (a * b) % p
        b = (b * b) % p
        k = k // 2

    return a

def zadanie3(n):
    temp = []
    for x in range(0, 10):
        b = random.randint(2, n - 1)
        if nwd(b, n) == 1:
            temp.append(b)
    for x in temp:
        if (zadanie2(n, (n - 1), x)) != 1:
            return False
    return True

def zadanie4(b, p):
    ls = zadanie2(b, ((p - 1) // 2), p)
    return True if ls == 1 else False

def zadanie5(b, p):
       return zadanie2(b, ((p + 1) // 4), p)

